import React from "react";
import useAccessibility from "../hooks/useAccessibility";

const CursorControl = () => {
    const { settings, updateSetting } = useAccessibility();

  const handleCursorChange = (event) => {
    updateSetting("cursorSize", event.target.value);
  };

  return (
    <div>
      <label>Cursor Size</label>
      <div>
        <input
          type="radio"
          id="defaultCursor"
          name="cursor"
          value="default"
          checked={settings.cursorSize === "default"}
          onChange={handleCursorChange}
        />
        <label htmlFor="defaultCursor">Default</label>
      </div>
      <div>
        <input
          type="radio"
          id="bigBlackCursor"
          name="cursor"
          value="big-black"
          checked={settings.cursorSize === "big-black"}
          onChange={handleCursorChange}
        />
        <label htmlFor="bigBlackCursor">Big Black Cursor</label>
      </div>
      <div>
        <input
          type="radio"
          id="bigWhiteCursor"
          name="cursor"
          value="big-white"
          checked={settings.cursorSize === "big-white"}
          onChange={handleCursorChange}
        />
        <label htmlFor="bigWhiteCursor">Big White Cursor</label>
      </div>
    </div>
  );
};

export default CursorControl;
