import React from "react";
import { useAccContext } from "../context/AccessibilityContext";

const DyslexicFontToggle = () => {
  const { settings, updateSetting } = useAccContext();

  const handleToggle = () => {
    updateSetting("dyslexicFont", !settings.dyslexicFont);
  };

  return (
    <div>
      <label htmlFor="dyslexicFontToggle">Dyslexic-Friendly Font</label>
      <input
        type="checkbox"
        id="dyslexicFontToggle"
        checked={settings.dyslexicFont}
        onChange={handleToggle}
      />
    </div>
  );
};

export default DyslexicFontToggle;
