import React from "react";
import { useAccContext } from "../context/AccessibilityContext";

const GreyscaleMode = () => {
  const { settings, updateSetting } = useAccContext();

  const handleToggle = () => {
    updateSetting("greyscale", settings.greyscale === 0 ? 100 : 0);
  };

  return (
    <div>
      <label htmlFor="greyscaleToggle">Greyscale Mode</label>
      <input
        type="checkbox"
        id="greyscaleToggle"
        checked={settings.greyscale === 100}
        onChange={handleToggle}
      />
    </div>
  );
};

export default GreyscaleMode;
