import React from "react";
import { useAccContext } from "../context/AccessibilityContext";

const HideImagesControl = () => {
  const { settings, updateSetting } = useAccContext();

  const handleToggle = () => {
    updateSetting("hideImages", !settings.hideImages);
  };

  return (
    <div>
      <label htmlFor="hideImagesToggle">Hide Images</label>
      <input
        type="checkbox"
        id="hideImagesToggle"
        checked={settings.hideImages}
        onChange={handleToggle}
      />
    </div>
  );
};

export default HideImagesControl;
