import React from "react";
import { useAccContext } from "../context/AccessibilityContext";

const HighlightLinks = () => {
  const { settings, updateSetting } = useAccContext();

  const handleToggle = () => {
    updateSetting("highlightLinks", !settings.highlightLinks);
  };

  return (
    <div>
      <label htmlFor="highlightLinksToggle">Highlight Links</label>
      <input
        type="checkbox"
        id="highlightLinksToggle"
        checked={settings.highlightLinks}
        onChange={handleToggle}
      />
    </div>
  );
};

export default HighlightLinks;
