import React from "react";
import { useAccContext } from "../context/AccessibilityContext";

const HighlightTitles = () => {
  const { settings, updateSetting } = useAccContext();

  const handleToggle = () => {
    updateSetting("highlightTitles", !settings.highlightTitles);
  };

  return (
    <div>
      <label htmlFor="highlightTitlesToggle">Highlight Titles</label>
      <input
        type="checkbox"
        id="highlightTitlesToggle"
        checked={settings.highlightTitles}
        onChange={handleToggle}
      />
    </div>
  );
};

export default HighlightTitles;
