import React from "react";
import { useAccContext } from "../context/AccessibilityContext";

const InvertColors = () => {
  const { settings, updateSetting } = useAccContext();

  const handleToggle = () => {
    updateSetting("invertColors", !settings.invertColors);
  };

  return (
    <div>
      <label htmlFor="invertColorsToggle">Invert Colors</label>
      <input
        type="checkbox"
        id="invertColorsToggle"
        checked={settings.invertColors}
        onChange={handleToggle}
      />
    </div>
  );
};

export default InvertColors;
