import React from "react";
import { useAccContext } from "../context/AccessibilityContext";

const LineHeightControl = () => {
  const { settings, updateSetting } = useAccContext();

  const handleChange = (event) => {
    const newLineHeight = event.target.value;
    updateSetting("lineHeight", newLineHeight);
  };

  return (
    <div>
      <label htmlFor="lineHeightControl">Line Height</label>
      <input
        type="range"
        id="lineHeightControl"
        min="1"
        max="3"
        step="0.1"
        value={settings.lineHeight}
        onChange={handleChange}
      />
      <span>{settings.lineHeight}</span>
    </div>
  );
};

export default LineHeightControl;
