import React from "react";
import { useAccContext } from "../context/AccessibilityContext";

const SaturationControl = () => {
  const { settings, updateSetting } = useAccContext();

  const handleSaturationChange = (event) => {
    const newSaturation = event.target.value;
    updateSetting("saturation", newSaturation);
    document.body.style.filter = `saturate(${newSaturation}%)`;
  };

  return (
    <div>
      <label htmlFor="saturationRange">Saturation</label>
      <input
        id="saturationRange"
        type="range"
        min="0"
        max="200"
        value={settings.saturation}
        onChange={handleSaturationChange}
      />
    </div>
  );
};

export default SaturationControl;
