export const applyAccessibilityStyles = (settings) => {
    document.documentElement.style.fontSize = `${settings.fontSize}px`;
    document.documentElement.style.lineHeight = settings.lineHeight;
    document.documentElement.style.filter = `contrast(${settings.contrast}%) brightness(${settings.brightness}%) 
                                             saturate(${settings.saturation}%) grayscale(${settings.greyscale}%)`;

    toggleClass("invert-colors", settings.invertColors);
    toggleClass("dyslexic-font", settings.dyslexicFont);
    toggleClass("hide-images", settings.hideImages);
    toggleClass("highlight-titles", settings.highlightTitles);
    toggleClass("highlight-links", settings.highlightLinks);
    toggleClass("big-cursor", settings.cursorSize === "big");
    toggleClass("reading-line", settings.readingLine);
    toggleClass("reading-mass", settings.readingMass);

    // ✅ Apply cursor style
    document.body.classList.remove("big-black-cursor", "big-white-cursor");
    if (settings.cursorSize === "big-black") {
        document.body.classList.add("big-black-cursor");
    } else if (settings.cursorSize === "big-white") {
        document.body.classList.add("big-white-cursor");
    }

    // ✅ Apply animation control
    toggleClass("disable-animations", !settings.animationEnabled);
};

export const toggleClass = (className, condition) => {
    if (condition) {
        document.body.classList.add(className);
    } else {
        document.body.classList.remove(className);
    }
};
